		["Recompensa de un Ladrón"] = {
			["Maestro de Conocimiento del Festival"] = {
				["Esta llama, robada del fuego del hogar de Ventormenta, te inunda de calor. No conoces este poder tan primitivo, pero puede que (NAME) tenga más información..."] = 9365,
			},
			["Cuentacuentos del Festival"] = {
